// site.js

$(document).ready(function () {
    // Agregar clases de Bootstrap a la tabla
    $("table").addClass("table table-striped");

    // Agregar clases de Bootstrap a los botones
    $("a.btn").addClass("btn-sm btn-primary"); // Aplica estilo a los botones de tipo "a"
    $("input.btn").addClass("btn-sm btn-primary"); // Aplica estilo a los botones de tipo "input"

    // Agregar íconos a los botones
    $("a.btn-info").html('<i class="fa fa-info-circle"></i> Detalles');
    $("a.btn-warning").html('<i class="fa fa-edit"></i> Editar');
    $("a.btn-danger").html('<i class="fa fa-trash"></i> Eliminar');
});
